        <DragDropContext direction="vertical">
        <Droppable droppableId="droppable-id">
          {(provided, snapshot) => (
            <div
              ref={provided.innerRef}
              style={getListStyle(snapshot.isDraggingOver)}
              {...provided.droppableProps}
              className="container heroCard text-center ">
            <div className="row">
                {
                    data.map((rec) => {
                        return(
                            <Draggable draggableId="droppable1" index={0}>
                                {(provided, snapshot) => (
                                    <div
                                    ref={provided.innerRef}
                                    {...provided.draggableProps}
                                    {...provided.dragHandleProps}
                                    style={getItemStyle(
                                        snapshot.isDragging,
                                        provided.draggableProps.style
                                    )}
                                    >
                                    <div className="col-lg-4 col-xl-4 col-xxl-4">
                                            <div className="card mx-auto" style={{width: "14rem"}}>
                                                <div className="card-body">
                                                        <h5 className="card-title crdt">{rec.title}</h5><span className="heroLogo"><img src={rec.logo} alt=""></img></span>
                                                        <h3 className="text-center cardValue">{rec.val}</h3>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                                </Draggable>
                            )})
                }
                </div>
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>

        </>
    )
}

export default Dr
