    for(let i=0; i<Math.ceil((crypto.length)/5); i++){
      page.push(i);
    }
    const IndexOfLastpage = currentPage * 5;
    const IndexOfFirstpage = IndexOfLastpage - 5;
    const CurrentItem = crypto.slice(IndexOfFirstpage - IndexOfLastpage);  

// }

      const handleClick = (e) =>{
        e.preventDefault();
        const Cname = e.currentTarget.id;
        Object.entries(crypto).map((val)=>{
            if(Cname == val[1].name){
              console.log(val[1])
            }
        })
    }

    const handlePage = (event) =>{
        setCurrentpage(Number(event.target.id))
    }

    return (
        <>
             <div className="container mt-4 text-center">
                <table className="table">
                  <thead className="bg-danger">
                    <tr>
                      <th scope="col">COMPANY NAME</th>
                      <th scope="col">SYMBOL</th>
                      <th scope="col">MARKET CAP</th>
                      <th scope="col"></th>
                      <th scope="col">CURRENT PRICE</th>
                    </tr>
                  </thead>
                  <tbody>
                  {
                    crypto?
                    Object.entries(CurrentItem).map((val) => {
                      return(
                              <tr >
                                <th scope="row" >{val[1].name}</th>
                                <td>{val[1].symbol}</td>
                                <td>{val[1].market_cap}</td>
                                <td> <button className="btn btn-primary" id={val[1].name} onClick={handleClick}> view</button>  </td>
                                <td>{val[1].price}</td>
                              </tr>
                            ) 
                          }) :null

                        }
                            <tr>
                                <th colSpan="5" className="bg-danger " >
                                    <div className="navigater d-flex justify-content-end me-5 text-white">
                                      <span className="me-5">1-5 of 276</span>  
                                      <span className="me-5">{'<'}</span>    
                                      <span className="me-5" onClick={handlePage}>{ '>'}</span>
                                    </div>
                                </th>
                            </tr>
                    </tbody>
               </table>
          </div>

{
  crypto?
  Object.entries(crypto).map((val)=>console.log(val[1]))
  :null
}
        </>
    )
}



export default Table
