import React, { useState } from 'react'
import ReactDOM from 'react-dom';
import './css/heroCard.css'
import gLogo from './img/GOOGL.png'
import fb from './img/FB.png'
import am from './img/AMZN.svg'
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
function Dr(props) {
    const data = [
        {
            id:"1",
            title:"googl",
            logo:gLogo,
            val:"1515"
        },
        {
            id:"2",
            title:"fb",
            logo:fb,
            val:"1515"
        },
        {
            id:"3",
            title:"amzn",
            logo:am,
            val:"1515"
        }
    ]


    const getItemStyle = (isDragging, draggableStyle) => ({

        background: isDragging ? 'lightgreen' : 'grey',
        ...draggableStyle,
      });

      const getListStyle = isDraggingOver => ({
        background: isDraggingOver ? 'lightblue' : 'lightgrey',
        overflow: 'auto',
      });
    return (
        <>
     <DragDropContext onDragEnd={(param) => {
        const srcI = param.source.index;
        const desI = param.source.index;
        if (desI) {
          data.splice(desI, 0, data.splice(srcI, 1)[0]);
          // data.saveList(data);
        }
        }}>
        <Droppable droppableId="droppable">
          {(provided, snapshot) => (
            <div
              ref={provided.innerRef}
              style={getListStyle(snapshot.isDraggingOver)}
              {...provided.droppableProps}>
                {
                    data.map((rec,index) => (
                            <Draggable key={rec.id} draggableId={rec.id} index={index}>
                                {(provided, snapshot) => (
                                    <div
                                    ref={provided.innerRef}
                                    {...provided.draggableProps}
                                    {...provided.dragHandleProps}
                                    style={getItemStyle(
                                        snapshot.isDragging,
                                        provided.draggableProps.style
                                    )}
                                    >
                                    <div className="col-lg-4 col-xl-4 col-xxl-4">
                                            <div className="card mx-auto" style={{width: "14rem"}}>
                                                <div className="card-body">
                                                        <h5 className="card-title crdt">{rec.title}</h5><span className="heroLogo"><img src={rec.logo} alt=""></img></span>
                                                        <h3 className="text-center cardValue">{rec.val}</h3>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                                </Draggable>
                    ))
                }
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>

        </>
    )
}

export default Dr
