import React, { useEffect, useLayoutEffect, useState } from 'react'
import ReactPaginate from 'react-paginate';

function Table() {
  const [crypto, setCrypto] = useState();
  const [rate, setRate] = useState();
  const [currentPage, setCurrentpage] = useState(1);
  const [loading, setLoading] = useState(false)

  useEffect( () =>{
    setLoading(true);
    fetch("https://api.nomics.com/v1/currencies/ticker?key=4704ee24ea5d850ad32c0644b21d284cf547af2a&interval=1d,30d&convert=EUR&per-page=100&page=1")
    .then(response => response.json())
    .then((data)=>{setCrypto(data);console.log(crypto)});
},[])

    if(crypto != null){
      console.log(crypto.length)
      const page = [];
      for(let i=0; i<Math.ceil((crypto.length)/5); i++){
      page.push(i);
    }
    console.log(page)
    const handleClick = (e) =>{
      e.preventDefault();
      const Cname = e.currentTarget.id;
      Object.entries(crypto).map((val)=>{
          if(Cname == val[1].name){
            console.log(val[1])
          }
      })
  }

  const handlePage = (event) =>{
    console.log(event.target.id)
    setCurrentpage((event.target.id))
  }
    const IndexOfLastpage = currentPage * 5;
    const IndexOfFirstpage = IndexOfLastpage - 5;
    const CurrentItem = crypto.slice(IndexOfFirstpage - IndexOfLastpage);  
    console.log(CurrentItem)

        return(
          <div>
                  <div className="container mt-4 text-center">
                <table className="table">
                  <thead className="bg-danger">
                    <tr>
                      <th scope="col">COMPANY NAME</th>
                      <th scope="col">SYMBOL</th>
                      <th scope="col">MARKET CAP</th>
                      <th scope="col"></th>
                      <th scope="col">CURRENT PRICE</th>
                    </tr>
                  </thead>
                  <tbody>
                  {
                    crypto?
                    Object.entries(CurrentItem).map((val,i) => {
                      i *= 1;
                      return(
                              <tr >
                                <th scope="row" key={Number} id={i} onClick={(e)=>console.log(e.target.id)}>{val[1].name}</th>
                                <td>{val[1].symbol}</td>
                                <td>{val[1].market_cap}</td>
                                <td> <button className="btn btn-primary" id={val[1].name} onClick={handleClick}> view</button>  </td>
                                <td>{val[1].price}</td>
                              </tr>
                            ) 
                          }) :null

                        }
                            <tr>
                                <th colSpan="5" className="bg-danger " >
                                    <div className="navigater d-flex justify-content-end me-5 text-white">
                                      <span className="me-5">1-5 of 276</span>  
                                      <span className="me-5">{'<'}</span>    
                                      <span className="me-5" onClick={handlePage}>{'>'}</span>
                                    </div>
                                </th>
                            </tr>
                    </tbody>
               </table>
          </div>
          </div>
        )
    }
    return(
      <div>

      </div>
    )

}



export default Table
